<?phpinclude("../../app/conexao.php");//entrada de formulário$nome = $_POST['nome'];$cpf = $_POST['cpf'];$senha = $_POST['senha'];$telefone=  $_POST['telefone'];$id_pai =  $_POST['cidade'];$veiculo = $_POST['veiculo'];$placa = $_POST['placa'];$busca = mysqli_query($conexao, "SELECT * FROM motorista_docs WHERE cpf = '$cpf'");$linhas = mysqli_num_rows($busca);if($linhas>0){    header("Location: cadastra_user.php?erro=1");}else{        //parametros para imagem        include "Upload.class.php";        $pasta='../../painel/uploads/';                //$permitido=array('image/jpg','image/jpeg','image/pjpeg', 'image/png'); //extensões permitidas        $img_cnh=$_FILES['img_cnh'];        $img_documento = $_FILES['img_documento'];        $img_lateral = $_FILES['img_lateral'];        $img_frente = $_FILES['img_frente'];        $img_selfie = $_FILES['img_selfie'];                $imagens = array(            $img_cnh, $img_documento, $img_lateral, $img_frente, $img_selfie        );        $imagens_fim= array();        if(is_dir($pasta)){        for ($index = 0; $index < count($imagens); $index++){                $upload = new Upload($imagens[$index], 800, 800, $pasta);        					$imagens_fim[] = $upload->salvar();            }        }                //echo json_encode($imagens_fim);                $create = "CREATE TABLE IF NOT EXISTS motorista_docs           (id int NOT NULL auto_increment, id_pai VARCHAR(255), nome VARCHAR(255), cpf VARCHAR(255), senha VARCHAR(255),        telefone VARCHAR(255),  veiculo VARCHAR(255), placa VARCHAR(255),        img_cnh VARCHAR(255), img_documento VARCHAR(255), img_lateral VARCHAR(255), img_frente VARCHAR(255),         img_selfie VARCHAR(255), PRIMARY KEY (id))";        $result = mysqli_query($conexao, $create) ;                 $sql = "INSERT INTO motorista_docs (              id_pai, nome, cpf, senha, telefone, veiculo, placa, img_cnh, img_documento, img_lateral, img_frente, img_selfie) VALUES            ('$id_pai', '$nome', '$cpf', '$senha', '$telefone', '$veiculo', '$placa', '$imagens_fim[0]', '$imagens_fim[1]', '$imagens_fim[2]', '$imagens_fim[3]', '$imagens_fim[4]')";        $salvar = mysqli_query($conexao, $sql);                if($salvar){            header("Location: cadastra_user.php?ok=cadastrado");        }}    mysqli_close($conexao);?>